    if (!room.tickInterval) room.startTick();
  });

  // Handle game messages (same protocol as before)
  socket.on('game', (message) => {
    const roomId = socket.currentRoomId;
    if (!roomId) return;
    const room = roomManager.getRoom(roomId);
    if (!room) return;
    const player = room.players.get(socket.id);
    if (!player) return;
    room.processMessage(socket, player, message);
  });

  // Handle room leave
  socket.on('leaveRoom', () => { ... });

  // Handle disconnect
  socket.on('disconnect', () => {
    const roomId = socket.currentRoomId;
    if (roomId) {
      const room = roomManager.getRoom(roomId);
      if (room) {
        const player = room.players.get(socket.id);
        if (player) {
          room.broadcastAll('PD' + player.id); // but scoped to room via io.to
          room.players.delete(socket.id);
          if (room.roundState.state === 'active') room.checkRoundEnd();
          // Destroy room if empty
          if (room.players.size === 0) {
            room.stopTick();
            roomManager.removeRoom(roomId);
          }
          broadcastRoomPlayerList(room);
        }
      }
    }
  });
});
