Room.prototype.resetRound = function() {
  // Clear bombs, items, timers (same as current)
  // ...

  // Re-randomize theme if needed
  if (this.themeId === 'random') {
    this.activeTheme = pickRandomTheme();
  }

  // Regenerate map
  this.cells.length = 0;
  this.initializeMap();

  // Reset all players (same as current but using this.players)
  // ...

  // Broadcast theme FIRST (so client can preload tileset)
  this.broadcastAll('TH' + this.activeTheme);

  // Then round reset signal and new map
  this.broadcastAll('RR');
  this.broadcastAll('WL' + MAP_WIDTH + '|' + MAP_HEIGHT + '|' + this.getMapData());

  // Send entity data (PA messages)
  // Use io.sockets.sockets.get(socketId) to get each socket
  for (const [socketId, player] of this.players) {
    const socket = this.io.sockets.sockets.get(socketId);
    if (!socket) continue;
    // Send self
    this.sendTo(socket, 'PA' + player.id + '|' + player.x + '|' + player.y + '|' + ...);
    // Send others
    for (const [otherSid, otherPlayer] of this.players) {
      if (otherSid === socketId) continue;
      this.sendTo(socket, 'PA' + otherPlayer.id + '|' + ...);
    }
  }

  // Set state to waiting, check auto-start
  this.roundState.state = 'waiting';
  if (this.players.size >= MIN_PLAYERS_TO_START) {
    setTimeout(() => {
      if (this.roundState.state === 'waiting' && this.players.size >= MIN_PLAYERS_TO_START) {
        this.startRound();
      }
    }, 2000);
  }
};
```

